mapper.select:
select

from sys_menu


    id = #{t.id,jdbcType=VARCHAR}


    and parent_id = #{t.parentId,jdbcType=VARCHAR}


    and parent_ids = #{t.parentIds,jdbcType=VARCHAR}


    and name = #{t.name,jdbcType=VARCHAR}


    and sort = #{t.sort,jdbcType=DECIMAL}


    and href = #{t.href,jdbcType=VARCHAR}


    and target = #{t.target,jdbcType=VARCHAR}


    and icon = #{t.icon,jdbcType=VARCHAR}


    and is_show = #{t.isShow,jdbcType=CHAR}


    and permission = #{t.permission,jdbcType=VARCHAR}


    and create_by = #{t.createBy,jdbcType=VARCHAR}


    and create_date = #{t.createDate,jdbcType=TIMESTAMP}


    and update_by = #{t.updateBy,jdbcType=VARCHAR}


    and update_date = #{t.updateDate,jdbcType=TIMESTAMP}


    and remarks = #{t.remarks,jdbcType=VARCHAR}


    and del_flag = #{t.delFlag,jdbcType=CHAR}
